             , CAR_ID
             , RESERVATION_DATE
             , START_TIME
             , END_TIME
             , DESTINATION
             , RESERVATION_STATUS
             ) 
        VALUES
             (
               #{reservationNo}
             , #{userNo}
             , #{carId}
             , SYSDATE
			 , #{startTime}
			 , #{endTime}
             , #{destination}
             , #{reservationStatus}
        	 )
